INSERT INTO `drupal_locales_target` VALUES (16519,'Syslog jest narzędziem systemu operacyjnego, które zapewnia dostęp do cennych informacji, pomocnych w zarządzaniu systemem i audytach bezpieczeństwa. Program ten jest odpowiedni głównie dla średnich i dużych witryn. Pozwala on stosować kryteria filtrowania komunikatów. W systemach UNIX i Linux kryteria te zapisane są w pliku /etc/syslog.conf. W systemie Microsoft Windows wszystkie komunikaty kierowane są do <em>Dziennika zdarzeń</em>. Więcej informacji na ten temat znajduje się na stronach <a href=\"@syslog_conf\">dotyczących syslog.conf</a> oraz funkcji <a href=\"@php_openlog\">openlog</a> i <a href=\"@php_syslog\">syslog</a> preprocesora PHP.','pl',0,0,0);
