INSERT INTO `drupal_locales_target` VALUES (16868,'Wskazanie więcej niż jednego terminu nadrzędnego względem aktualnie edytowanego terminu spowoduje, że w przypadku każdego z terminów słownika %vocabulary poszukiwany będzie więcej niż jeden termin nadrzędny. Nie będzie również możliwe korzystanie z metody \"przeciągnij i upuść\" do zmiany układu terminów. W związku z tym ustalenie terminów nadrzędnych będzie się odbywać jedynie poprzez formularz edycji.','pl',0,0,0);
