INSERT INTO `drupal_locales_target` VALUES (16918,'Stosuj automatyczne zapobieganie przeciążeniu dla użytkowników anonimowych','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16919,'Automatyczne zapobieganie przeciążeniu może być włączane, kiedy z witryny zacznie korzystać określona liczba użytkowników anonimowych. Na przykład, jeśli ma się tak dziać, kiedy witrynę przegląda 250 użytkowników anonimowych, trzeba wprowadzić liczbę \"250\". Jeśli automatyczne zapobieganie przeciążeniu dla użytkowników uwierzytelnionych nie jest pożądane, nie należy wypełniać pola (lub można wprowadzić liczbę 0). Liczbę użytkowników anonimowych obecnie korzystających z witryny podaje blok \"Kto przegląda\".','pl',0,0,0);
