INSERT INTO `drupal_locales_target` VALUES (16947,'Moduł historii służy do wyświetlania ostatnio dodanych i zaktualizowanych wpisów. Umożliwia także wyświetlanie historii wpisów konkretnych użytkowników.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16948,'Strona zawierająca listę najnowszych i ostatnio zmodyfikowanych wpisów dostępna jest poprzez odnośnik <em>Ostatnio dodane</em>, który znaduje się w menu nawigacyjnym. W tabeli prezentowane są informacje o rodzaju zawartości, tytule, nazwisku bądź pseudonimie autora, liczbie odpowiedzi i dacie ostatniej aktualizacji (ostatniej zmiany treści lub nowych odpowiedziach). Aby skorzystać z modułu w celu śledzenia dodawanych przez użytkownika wpisów, należy przejść na kartę <em>Historia</em> na stronie profilu danego użytkownika.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16949,'Więcej informacji na <a href=\"@tracker\">stronie dotyczącej modułu historii</a> w podręczniku konfiguracji w internecie.','pl',0,0,0);
