INSERT INTO `drupal_locales_target` VALUES (16998,'Umożliwia wyzwalanie działań zautomatyzowanych, kiedy w systemie wystąpią określone zdarzenia, takie jak dodanie wpisu.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16999,'Wyzwalacz','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17000,'Wyzwalacze to zdarzenia występujące w systemie, takie jak dodanie nowego wpisu bądź zalogowanie się użytkownika. Moduł służy do wiązania tych zdarzeń ze zautomatyzowanymi działaniami, takimi jak wstrzymywanie publikacji lub wysyłanie wiadomości e-mail do administratora witryny. Na <a href=\"@url\">stronie działań zautomatyzowanych</a> znajduje się lista działań, a także formularz pozwalający dodać i skonfigurować nowe działania.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17001,'Niniejsza strona pozwala wiązać działania ze zdarzeniami dotyczącymi odpowiedzi. Można na przykład umieszczać na stronie głównej wszystkie wpisy, do których dodawana jest odpowiedź.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17002,'Niniejsza strona pozwala wiązać działania ze zdarzeniami dotyczącymi zawartości witryny. Można na przykład wysyłać e-mail do administratora, gdy tworzony lub aktualizowany jest wpis.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17003,'Niniejsza strona pozwala wiązać działania ze zdarzeniami dotyczącymi <a href=\"@cron\">zadań cron</a>.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17004,'Niniejsza strona pozwala wiązać działania ze zdarzeniami dotyczącymi taksonomii. Można na przykład wysyłać e-mail do administratora, gdy jeden z terminów słownika jest usuwany.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17005,'Niniejsza strona pozwala wiązać działania ze zdarzeniami dotyczącymi użytkowników. Można na przykład wysyłać e-mail do administratora, gdy tworzone jest nowe konto użytkownika.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17006,'Moduł wyzwalaczy umożliwia wyzwalanie działań zautomatyzowanych, gdy w systemie wystąpią określone zdarzenia, takie jak dodanie nowego wpisu czy zalogowanie się użytkownika.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17007,'Wyzwalacze oraz działania zautomatyzowane mogą wykonywać wiele przydatnych czynności, takich jak wysyłanie administratorowi wiadomości e-mail, gdy konto użytkownika jest usuwane, albo automatyczne wstrzymywanie publikacji odpowiedzi, które zawierają określone słowa. Jest pięć standardowych rodzajów zdarzeń - zdarzenia dotyczące odpowiedzi, zawartości, zadań cron, taksonomii oraz użytkowników. Moduły dodatkowe mogą definiować inne rodzaje zdarzeń.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17008,'Więcej informacji znajduje się na <a href=\"@trigger\">stronie dotyczącej modułu wyzwalaczy</a>.','pl',0,0,0);
