INSERT INTO `drupal_locales_target` VALUES (17033,'Strona zawiera informacje o nowych wersjach zainstalowanych modułów lub skórek. Każdy moduł czy skórka wchodzi w skład tak zwanego projektu, którego nazwa może, lecz nie musi, być identyczna. Projekty mogą się składać z wielu modułów lub skórek.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17034,'Do zwiększania funkcjonalności lub zmiany wyglądu witryny służą <a href=\"@modules\">dodatkowe moduły</a> i <a href=\"@themes\">skórki</a>.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17035,'Informacje o wydaniu nowych wersji zainstalowanych modułów lub skórek można znaleźć na <a href=\"@available_updates\">stronie o aktualizacjach</a>.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17036,'Moduł okresowo sprawdza, czy nie pojawiły się nowe wersje zainstalowanego oprogramowania (w tym dodatkowych modułów i skórek) i powiadamia administratora, jeśli tak się stało.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17037,'Informacje na temat nowych wersji wyświetlane są na <a href=\"@update-report\">stronie dotyczącej nowych wersji</a>. Częstotliwość zapytań i powiadomień można ustalić na <a href=\"@update-settings\">stronie ustawień modułu</a>.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17038,'W celu zapewnienia tych informacji, do drupal.org wysyłane są anonimowe statystyki dotyczące zainstalowanego Drupala. Moduł można wyłączyć na <a href=\"@modules\">stronie zarządzania modułami</a>.','pl',0,0,0);
