INSERT INTO `drupal_locales_target` VALUES (17307,'Dziękujemy za utworzenie konta !username w witrynie !site. Można się teraz do niego zalogować, wprowadzając na stronie !login_uri poniższe dane.\n\nNazwa użytkownika: !username\nHasło: !password\n\nLogowanie może także zostać przeprowadzone poprzez odwiedzenie poniższej strony.\n\n!login_url\n\nLogowanie z użyciem powyższych danych można przeprowadzić tylko raz.\n\nStrona !edit_uri która ukaże się po zalogowaniu, umożliwia zmianę hasła.\n\n\n--  !site','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17308,'Administrator witryny !site stworzył konto użytkownika','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17309,'Administrator witryny zaprasza do skorzystania z konta !username. Można się teraz do niego zalogować, wprowadzając na stronie !login_uri poniższe dane.\n\nNazwa użytkownika: !username\nHasło: !password\n\nLogowanie może także zostać przeprowadzone poprzez odwiedzenie poniższej strony.\n\n!login_url\n\nLogowanie z użyciem powyższych danych można przeprowadzić tylko raz.\n\nStrona !edit_uri która ukaże się po zalogowaniu, umożliwia zmianę hasła.\n\n\n--  !site','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17310,'Informacje dotyczące konta !username, oczekującego na zatwierdzenie przez administratora !site','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (17311,'Dziękujemy za utworzenie konta !username w witrynie !site. Oczekuje ono w tej chwili na zatwierdzenie. Kiedy zostanie ono wydane, wyślemy kolejną wiadomość e-mail, która będzie zawierać informacje potrzebne do zalogowania i zmiany hasła.\n\n\n-- !site','pl',0,0,0);
